import React from "react";
import "../../styled/Header.css";

const Header = ({ onAuth }) => {
  const auth = onAuth && <button onClick={onAuth}>Sign in as Admin</button>;

  const logout = !onAuth && <button onClick={onAuth}>Logout</button>;

  const authLogout = () => {
    if (onAuth) return "sign in as Admin";

    return "Logout";
  };

  return (
    <div>
      <div className="header">ShopName</div>
      <button onClick={onAuth}>{authLogout()}</button>
    </div>
  );
};

export default Header;
