import React from "react";
import "../../styled/Header.css";

const Header = ({ onAuth, isAuth }) => {
  const auth = onAuth && <button onClick={onAuth}>Sign in as Admin</button>;

  const logout = !onAuth && <button onClick={onAuth}>Logout</button>;

  const authLogout = () => {
    if (onAuth) return <button onClick={onAuth}>Sign in as Admin</button>;

    return <button onClick={onAuth}>Logout</button>;
  };

  return (
    <div>
      <div className="header">ShopName</div>
      {authLogout()}
    </div>
  );
};

export default Header;
